class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      data: ['Erin Abbey', 'Nguyen Ngoc Huyen', 'Jvf']
    }
    this.handledAdd = this.handledAdd.bind(this);
  };
  handledAdd(){
    var newitem = this.state.data.concat([prompt('Add new item')]);
    this.setState ({data: newitem});
  }
  handledRemove(i){
    var newitem = this.state.data.slice();
    newitem.splice(i, 1);
    this.setState({data: newitem});
  }
  render(){
    var data =this.state.data.map(function(item, i){
      return(
        <div key = {item} onClick = {this.handledRemove.bind(this,  i)}>{item}</div>
        );
      }.bind(this));

    return(
    <div>
    <button onClick = {this.handledAdd}>Add item</button>
    <ReactCSSTransitionGroup transitiontime = "example" transitionentertimeout ={500} transileave ={500}>{data}</ReactCSSTransitionGroup>
    </div>
    );
  }
}
export default App;
